using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using Wismapi.Data;
using Wismapi.Data.Entity;

namespace AIO.IDOS3.Data.Entity
{

    public partial class UserDeviceLocationTableProvider : DataTableProvider<UserDeviceLocation>
    {

        #region Methods

        protected override async Task OnBeginInsertDataAsync(BeginOperationDataEventArgs<UserDeviceLocation> e)
        {
            e.Data.CreatedDate = await DbContext.GetDatabaseUtcDateTimeAsync();

            var authenticatedUser = ((MainDbContext)DbContext).GetAuthenticatedUser();
            if (authenticatedUser != null)
                e.Data.CreatedByUserID = authenticatedUser.ID;

            await base.OnBeginInsertDataAsync(e);
        }

        #endregion
        
    }

