        private async Task ProcessLocalData(DateTime? syncDate, int status, int attempts, mvCollectionCall serverData, mvCollectionCall obj,
            mvCollectionCallTableProvider tableProvider, bool useTransaction)
        {
            var localDate = (syncDate.HasValue) ? syncDate.Value : await DbContext.GetDatabaseUtcDateTimeAsync();
            mvCollectionCall salesCall = null;

            if (serverData == null)
            {
                salesCall = new mvCollectionCall();
                salesCall.ID = obj.ID;

                await tableProvider.DeleteDataAsync(salesCall, useTransaction);
            }
            else
            {
                var isInsert = false;
                salesCall = await tableProvider.GetDataAsync(serverData.ID);
                if (salesCall == null)
                {
                    isInsert = true;
                    salesCall = new mvCollectionCall();
                }                

                salesCall.CopyFrom(serverData);
                DbContext.SetSyncData(salesCall, syncDate, localDate, status, attempts);

                if (isInsert)
                    await tableProvider.InsertDataAsync(salesCall, useTransaction);
                else
                    await tableProvider.UpdateDataAsync(salesCall, useTransaction);

                if (obj != null)
                {
                    obj.CopyFrom(serverData);
                    DbContext.CopySyncData(obj, salesCall);
                }
            }
        }

