using Microsoft.EntityFrameworkCore;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Linq.Expressions;
using System.Threading.Tasks;
using Wismapi.Data.Entity;

namespace AIO.IDOS3.Mobile.Data.Entity
{

    public partial class mvCollectorSyncProvider : DataSyncProvider<mvCollector>
    {

        #region Properties

        public static string Selector = "ID,Code,Name,Collector,IsHeadOffice,SiteID,StatusID,StatusName,CreatedDate,ModifiedDate,IsDeleted";

        #endregion

        #region Methods

        private async Task<Expression<Func<mvCollector, bool>>> GetFilter()
        {
            var employeeID = await DbContext.GetDataTableProvider<mvEmployeeTableProvider>().GetData().Select(p => p.ID).SingleAsync();

            Expression filter = null;
            var type = typeof(mvCollector);
            var param = Expression.Parameter(type, "param");

            var propEmployeeID = Expression.Property(param, "EmployeeID");
            filter = Expression.MakeBinary(ExpressionType.Equal, propEmployeeID, Expression.Constant(employeeID, type.GetProperty("EmployeeID").PropertyType));

            return Expression.Lambda<Func<mvCollector, bool>>(filter, param);
        }



        protected override Exception OnDownloadAllData(bool continueOnError) { return OnDownloadAllDataAsync(continueOnError).Result; }
        protected override Exception OnUploadAllData(bool continueOnError) { return OnUploadAllDataAsync(continueOnError).Result; }
        protected override Exception OnDownloadData(mvCollector obj, bool useTransaction) { return OnDownloadDataAsync(obj, useTransaction).Result; }
        protected override Exception OnUploadData(mvCollector obj, bool useTransaction) { return OnUploadDataAsync(obj, useTransaction).Result; }


        protected override async Task<Exception> OnDownloadAllDataAsync(bool continueOnError)
        {
            Exception exception = null;

            var collectorTableProvider = DbContext.GetDataTableProvider<mvCollectorTableProvider>();
            var collectorServiceProvider = DataServiceContext.GetDataServiceProvider<mvCollectorServiceProvider>();

            DateTime? syncDate = null;

            List<mvCollector> serverDataList = null;

            var minSyncDate = (DateTimeOffset?)await collectorTableProvider.GetData().MinAsync(p => p.SyncDate);

            for (int i = 0; i < DbContext.SyncMaxAttempts; i++)
            {
                try
                {
                    if (!syncDate.HasValue) syncDate = await DataServiceContext.GetDatabaseServerUtcDateTimeAsync();

                    var query = collectorServiceProvider.GetData().AddQueryOption("$select", Selector).Where(await GetFilter());

                    if (minSyncDate.HasValue)
                        query = query.Where(p => (p.CreatedDate > minSyncDate.Value) || ((p.ModifiedDate != null) && (p.ModifiedDate.Value > minSyncDate.Value)));

                    serverDataList = query.ToList();
                    break;
                }
                catch (Exception ex)
                {
                    if (i == (DbContext.SyncMaxAttempts - 1)) exception = ex;
                    else await Task.Delay(DbContext.SyncAttemptDelay);
                }
            }

            if (serverDataList != null)
            {
                if (serverDataList.Count > 0)
                {
                    var sites = await DbContext.GetDataTableProvider<mvSiteTableProvider>().GetData().ToListAsync();

                    int index = 0;
                    foreach (var serverData in serverDataList)
                    {
                        try
                        {
                            var site = (serverData.IsHeadOffice) ? null : sites.Where(p => p.ID.Equals(serverData.SiteID)).SingleOrDefault();

                            AssignFromLocalData(serverData, site);

                            await ProcessLocalData(syncDate, 1, 1, serverData, null, collectorTableProvider, true);
                        }
                        catch (Exception ex)
                        {
                            if (!continueOnError)
                            {
                                exception = ex;
                                break;
                            }
                        }

                        index++;
                    }
                }

                await DbContext.UpdateAllSyncDateAsync<mvCollector>(syncDate.Value);
            }

            return exception;
        }

        protected override async Task<Exception> OnUploadAllDataAsync(bool continueOnError)
        {
            Exception exception = new NotImplementedException();

            return await Task.FromResult(exception);
        }


        protected override async Task<Exception> OnDownloadDataAsync(mvCollector obj, bool useTransaction)
        {
            Exception exception = null;

            var collectorTableProvider = DbContext.GetDataTableProvider<mvCollectorTableProvider>();
            var collectorServiceProvider = DataServiceContext.GetDataServiceProvider<mvCollectorServiceProvider>();

            DateTime? syncDate = null;
            int status = 1;
            int attempts = 0;

            mvCollector serverData = null;

            for (int i = 0; i < DbContext.SyncMaxAttempts; i++)
            {
                try
                {
                    if (!syncDate.HasValue) syncDate = await DataServiceContext.GetDatabaseServerUtcDateTimeAsync();

                    serverData = collectorServiceProvider.GetData().AddQueryOption("$select", Selector)
                        .Where(p => p.ID.Equals(obj.ID)).SingleOrDefault();
                    break;
                }
                catch (Exception ex)
                {
                    if (i == (DbContext.SyncMaxAttempts - 1)) { exception = ex; status = 0; }
                    else await Task.Delay(DbContext.SyncAttemptDelay);
                }
            }

            if (serverData != null)
            {
                var site = (serverData.IsHeadOffice) ? null : await DbContext.GetDataTableProvider<mvSiteTableProvider>().GetDataAsync(serverData.SiteID);

                AssignFromLocalData(serverData, site);
            }

            await ProcessLocalData(syncDate, status, attempts, serverData, obj, collectorTableProvider, useTransaction);

            return exception;
        }

        protected override async Task<Exception> OnUploadDataAsync(mvCollector obj, bool useTransaction)
        {
            Exception exception = new NotImplementedException();

            return await Task.FromResult(exception);
        }



        private async Task ProcessLocalData(DateTime? syncDate, int status, int attempts, mvCollector serverData, mvCollector obj,
            mvCollectorTableProvider tableProvider, bool useTransaction)
        {
            var localDate = (syncDate.HasValue) ? syncDate.Value : await DbContext.GetDatabaseUtcDateTimeAsync();
            mvCollector collector = null;

            if (serverData == null)
            {
                collector = new mvCollector();
                collector.ID = obj.ID;

                await tableProvider.DeleteDataAsync(collector, useTransaction);
            }
            else
            {
                var isInsert = false;
                var isDelete = false;
                collector = await tableProvider.GetDataAsync(serverData.ID);
                if (collector == null)
                {
                    isInsert = true;
                    collector = new mvCollector();
                }
                else if (collector.IsDeleted)
                    isDelete = true;

                collector.CopyFrom(serverData);
                DbContext.SetSyncData(collector, syncDate, localDate, status, attempts);

                if (isInsert)
                    await tableProvider.InsertDataAsync(collector, useTransaction);
                else if (isDelete)
                    await tableProvider.DeleteDataAsync(collector, useTransaction);
                else
                    await tableProvider.UpdateDataAsync(collector, useTransaction);

                if (obj != null)
                {
                    obj.CopyFrom(serverData);
                    DbContext.CopySyncData(obj, collector);
                }
            }
        }

        private void AssignFromLocalData(mvCollector serverData, mvSite site)
        {
            if (site != null)
            {
                serverData.SiteCode = site.Code;
                serverData.SiteName = site.Name;
                serverData.Site = site.Site;
                serverData.AreaID = site.AreaID;
                serverData.AreaCode = site.AreaCode;
                serverData.AreaName = site.AreaName;
                serverData.Area = site.Area;
                serverData.RegionID = site.RegionID;
                serverData.RegionCode = site.RegionCode;
                serverData.RegionName = site.RegionName;
                serverData.Region = site.Region;
                serverData.TerritoryID = site.TerritoryID;
                serverData.TerritoryCode = site.TerritoryCode;
                serverData.TerritoryName = site.TerritoryName;
                serverData.Territory = site.Territory;
                serverData.CompanyID = site.CompanyID;
                serverData.CompanyCode = site.CompanyCode;
                serverData.CompanyName = site.CompanyName;
                serverData.Company = site.Company;
                serverData.SiteDistributionTypeID = site.DistributionTypeID;
                serverData.SiteDistributionTypeName = site.DistributionTypeName;
                serverData.IsSiteProductLotCodeEntryRequired = site.IsProductLotCodeEntryRequired;
            }
        }

        #endregion

    }

}
