        private async Task<Expression<Func<mvDiscountStrata, bool>>> GetFilter()
        {
            var discountGroupProductTableProvider = DbContext.GetDataTableProvider<mvDiscountGroupProductTableProvider>();

            var discountStrata1IDs = await discountGroupProductTableProvider.GetData().Select(p => p.DiscountStrata1ID).Distinct().ToListAsync();
            var discountStrata2IDs = await discountGroupProductTableProvider.GetData().Select(p => p.DiscountStrata2ID).Distinct().ToListAsync();
            var discountStrata3IDs = await discountGroupProductTableProvider.GetData().Select(p => p.DiscountStrata3ID).Distinct().ToListAsync();
            var discountStrata4IDs = await discountGroupProductTableProvider.GetData().Select(p => p.DiscountStrata4ID).Distinct().ToListAsync();
            var discountStrata5IDs = await discountGroupProductTableProvider.GetData().Select(p => p.DiscountStrata5ID).Distinct().ToListAsync();

            var discountStrataIDs = new List<int?>();
            discountStrataIDs.AddRange(discountStrata1IDs);
            discountStrataIDs.AddRange(discountStrata2IDs);
            discountStrataIDs.AddRange(discountStrata3IDs);
            discountStrataIDs.AddRange(discountStrata4IDs);
            discountStrataIDs.AddRange(discountStrata5IDs);

            discountStrataIDs = discountStrataIDs.Distinct().ToList();

            Expression filter = null;
            var type = typeof(mvDiscountStrata);
            var param = Expression.Parameter(type, "param");

            var propID = Expression.Property(param, "ID");
            foreach (var id in discountStrataIDs)
            {
                if (!id.HasValue) continue;

                var expr = Expression.MakeBinary(ExpressionType.Equal, propID, Expression.Constant(id, type.GetProperty("ID").PropertyType));
                filter = (filter == null) ? expr : Expression.MakeBinary(ExpressionType.Or, filter, expr);
            }

            return Expression.Lambda<Func<mvDiscountStrata, bool>>(filter, param);
        }



        protected override Exception OnDownloadAllData(bool continueOnError) { return OnDownloadAllDataAsync(continueOnError).Result; }
        protected override Exception OnUploadAllData(bool continueOnError) { return OnUploadAllDataAsync(continueOnError).Result; }
        protected override Exception OnDownloadData(mvDiscountStrata obj, bool useTransaction) { return OnDownloadDataAsync(obj, useTransaction).Result; }
        protected override Exception OnUploadData(mvDiscountStrata obj, bool useTransaction) { return OnUploadDataAsync(obj, useTransaction).Result; }


        protected override async Task<Exception> OnDownloadAllDataAsync(bool continueOnError)
        {
            Exception exception = null;

            var discountStrataTableProvider = DbContext.GetDataTableProvider<mvDiscountStrataTableProvider>();
            var discountStrataServiceProvider = DataServiceContext.GetDataServiceProvider<mvDiscountStrataServiceProvider>();

            DateTime? syncDate = null;

            List<mvDiscountStrata> serverDataList = null;

            var minSyncDate = (DateTimeOffset?)await discountStrataTableProvider.GetData().MinAsync(p => p.SyncDate);

            for (int i = 0; i < DbContext.SyncMaxAttempts; i++)
            {
                try
                {
                    if (!syncDate.HasValue) syncDate = await DataServiceContext.GetDatabaseServerUtcDateTimeAsync();

                    var query = discountStrataServiceProvider.GetData().AddQueryOption("$select", Selector).Where(await GetFilter());

                    if (minSyncDate.HasValue)
                        query = query.Where(p => (p.CreatedDate > minSyncDate.Value) || ((p.ModifiedDate != null) && (p.ModifiedDate.Value > minSyncDate.Value)));
                        
                    serverDataList = query.ToList();
                    break;
                }
                catch (Exception ex)
                {
                    if (i == (DbContext.SyncMaxAttempts - 1)) exception = ex;
                    else await Task.Delay(DbContext.SyncAttemptDelay);
                }
            }

            if (serverDataList != null)
            {
                int index = 0;
                foreach (var serverData in serverDataList)
                {
                    try
                    {
                        await ProcessLocalData(syncDate, 1, 1, serverData, null, discountStrataTableProvider, true);
                    }
                    catch (Exception ex)
                    {
                        if (!continueOnError)
                        {
                            exception = ex;
                            break;
                        }
                    }

                    index++;
                }
                
                await DbContext.UpdateAllSyncDateAsync<mvDiscountStrata>(syncDate.Value);
            }

            return exception;
        }
        
