        protected override async Task<Exception> OnDownloadDataAsync(mvDiscountStrata obj, bool useTransaction)
        {
            Exception exception = null;

            var discountStrataTableProvider = DbContext.GetDataTableProvider<mvDiscountStrataTableProvider>();
            var discountStrataServiceProvider = DataServiceContext.GetDataServiceProvider<mvDiscountStrataServiceProvider>();

            DateTime? syncDate = null;
            int status = 1;
            int attempts = 0;

            mvDiscountStrata serverData = null;

            for (int i = 0; i < DbContext.SyncMaxAttempts; i++)
            {
                try
                {
                    if (!syncDate.HasValue) syncDate = await DataServiceContext.GetDatabaseServerUtcDateTimeAsync();

                    serverData = discountStrataServiceProvider.GetData().AddQueryOption("$select", Selector)
                        .Where(p => (p.Code.ToLower() == obj.Code.ToLower())).SingleOrDefault();
                    break;
                }
                catch (Exception ex)
                {
                    if (i == (DbContext.SyncMaxAttempts - 1)) { exception = ex; status = 0; }
                    else await Task.Delay(DbContext.SyncAttemptDelay);
                }
            }

            await ProcessLocalData(syncDate, status, attempts, serverData, obj, discountStrataTableProvider, useTransaction);

            return exception;
        }

