        private async Task ProcessLocalData(DateTime? syncDate, int status, int attempts, mvDiscountStrata serverData, mvDiscountStrata obj,
            mvDiscountStrataTableProvider tableProvider, bool useTransaction)
        {
            var localDate = (syncDate.HasValue) ? syncDate.Value : await DbContext.GetDatabaseUtcDateTimeAsync();
            mvDiscountStrata discountStrata = null;

            if (serverData == null)
            {
                discountStrata = new mvDiscountStrata();
                discountStrata.ID = obj.ID;

                await tableProvider.DeleteDataAsync(discountStrata, useTransaction);
            }
            else
            {
                var isInsert = false;
                var isDelete = false;
                discountStrata = await tableProvider.GetDataAsync(serverData.ID);
                if (discountStrata == null)
                {
                    isInsert = true;
                    discountStrata = new mvDiscountStrata();
                }                
                else if (discountStrata.IsDeleted)
                    isDelete = true;                    

                discountStrata.CopyFrom(serverData);
                DbContext.SetSyncData(discountStrata, syncDate, localDate, status, attempts);

                if (isInsert)
                    await tableProvider.InsertDataAsync(discountStrata, useTransaction);
                else if (isDelete)
                    await tableProvider.DeleteDataAsync(discountStrata, useTransaction);
                else
                    await tableProvider.UpdateDataAsync(discountStrata, useTransaction);

                if (obj != null)
                {
                    obj.CopyFrom(serverData);
                    DbContext.CopySyncData(obj, discountStrata);
                }
            }
        }

