using System;
using System.Collections.Generic;
using System.Threading.Tasks;
using Wismapi.Data;
using Wismapi.Data.Entity;

namespace AIO.IDOS3.Mobile.Data.Entity
{

    public partial class mvDiscountStrataTableProvider : DataTableProvider<mvDiscountStrata>
    {
        
        #region Methods

        protected override async Task OnEndInsertDataAsync(EndOperationDataEventArgs<mvDiscountStrata> e)
        {
            if (!e.IsError)
            {
                if (e.Data.ChildDetails != null)
                {
                    var childTableProvider = DbContext.GetDataTableProvider<mvDiscountStrataDetailsTableProvider>();

                    await InsertChilds(childTableProvider, e.Data.ID, e.Data.ChildDetails, e.UseTransaction);
                }                
            }

            await base.OnEndInsertDataAsync(e);
        }

        protected override async Task OnEndUpdateDataAsync(EndOperationDataEventArgs<mvDiscountStrata> e)
        {
            if (!e.IsError)
            {
                if (e.Data.ChildDetails != null)
                {
                    var childTableProvider = DbContext.GetDataTableProvider<mvDiscountStrataDetailsTableProvider>();

                    await DeleteChilds(childTableProvider, e.Data.ID, e.UseTransaction);
                    await InsertChilds(childTableProvider, e.Data.ID, e.Data.ChildDetails, e.UseTransaction);
                }
            }

            await base.OnEndUpdateDataAsync(e);
        }

        protected override async Task OnBeginDeleteDataAsync(BeginOperationDataEventArgs<mvDiscountStrata> e)
        {
            var childTableProvider = DbContext.GetDataTableProvider<mvDiscountStrataDetailsTableProvider>();

            await DeleteChilds(childTableProvider, e.Data.ID, e.UseTransaction);

            await base.OnBeginDeleteDataAsync(e);
        }



        private async Task InsertChilds(mvDiscountStrataDetailsTableProvider childTableProvider, int strataID, ICollection<mvDiscountStrataDetails> childs, bool useTransaction)
        {
            foreach (var child in childs)
            {
                child.StrataID = strataID;
                await childTableProvider.InsertDataAsync(child, useTransaction);
            }                
        }

        private async Task DeleteChilds(mvDiscountStrataDetailsTableProvider childTableProvider, int strataID, bool useTransaction)
        {
            var deleteChilds = childTableProvider.GetDataByStrataID(strataID);
            foreach (var child in deleteChilds)                
                await childTableProvider.DeleteDataAsync(child, useTransaction);
        }

        #endregion

    }

}
