        private async Task ProcessLocalData(DateTime? syncDate, int status, int attempts, mvFinanceInstitution serverData, mvFinanceInstitution obj,
            mvFinanceInstitutionTableProvider tableProvider, bool useTransaction)
        {
            var localDate = (syncDate.HasValue) ? syncDate.Value : await DbContext.GetDatabaseUtcDateTimeAsync();
            mvFinanceInstitution financeInstitution = null;

            if (serverData == null)
            {
                financeInstitution = new mvFinanceInstitution();
                financeInstitution.ID = obj.ID;

                await tableProvider.DeleteDataAsync(financeInstitution, useTransaction);
            }
            else
            {
                var isInsert = false;
                var isDelete = false;
                financeInstitution = await tableProvider.GetDataAsync(serverData.ID);
                if (financeInstitution == null)
                {
                    isInsert = true;
                    financeInstitution = new mvFinanceInstitution();
                }                
                else if (financeInstitution.IsDeleted)
                    isDelete = true;                    

                financeInstitution.CopyFrom(serverData);
                DbContext.SetSyncData(financeInstitution, syncDate, localDate, status, attempts);

                if (isInsert)
                    await tableProvider.InsertDataAsync(financeInstitution, useTransaction);
                else if (isDelete)
                    await tableProvider.DeleteDataAsync(financeInstitution, useTransaction);
                else
                    await tableProvider.UpdateDataAsync(financeInstitution, useTransaction);

                if (obj != null)
                {
                    obj.CopyFrom(serverData);
                    DbContext.CopySyncData(obj, financeInstitution);
                }
            }
        }

