        protected override async Task<Exception> OnUploadAllDataAsync(bool continueOnError)
        {
            Exception exception = null;

            var giroTableProvider = DbContext.GetDataTableProvider<mvGiroTableProvider>();
            var giroServiceProvider = DataServiceContext.GetDataServiceProvider<mvGiroServiceProvider>();

            DateTime? syncDate = null;

            var localDataList = await giroTableProvider.GetData().Where(p => (p.SyncDate == null) && !p.IsDeleted).ToListAsync();

            int index = 0;
            foreach (var obj in localDataList)
            {
