        private async Task ProcessLocalData(DateTime? syncDate, int status, int attempts, mvGiro serverData, mvGiro obj,
            mvGiroTableProvider tableProvider, bool useTransaction)
        {
            var localDate = (syncDate.HasValue) ? syncDate.Value : await DbContext.GetDatabaseUtcDateTimeAsync();
            mvGiro giro = null;

            if (serverData == null)
            {
                giro = new mvGiro();
                giro.ID = obj.ID;

                await tableProvider.DeleteDataAsync(giro, useTransaction);
            }
            else
            {
                var isInsert = false;
                var isDelete = false;
                giro = await tableProvider.GetDataAsync(serverData.ID);
                if (giro == null)
                {
                    isInsert = true;
                    giro = new mvGiro();
                }                
                else if (giro.IsDeleted)
                    isDelete = true;                    

                giro.CopyFrom(serverData);
                DbContext.SetSyncData(giro, syncDate, localDate, status, attempts);

                if (isInsert)
                    await tableProvider.InsertDataAsync(giro, useTransaction);
                else if (isDelete)
                    await tableProvider.DeleteDataAsync(giro, useTransaction);
                else
                    await tableProvider.UpdateDataAsync(giro, useTransaction);

                if (obj != null)
                {
                    obj.CopyFrom(serverData);
                    DbContext.CopySyncData(obj, giro);
                }
            }
        }

