        protected override Exception OnUploadData(mvMobileDeviceSettings obj, bool useTransaction) { return OnUploadDataAsync(obj, useTransaction).Result; }


        protected override async Task<Exception> OnDownloadAllDataAsync(bool continueOnError)
        {
            Exception exception = null;

            var mobileDeviceSettingsTableProvider = DbContext.GetDataTableProvider<mvMobileDeviceSettingsTableProvider>();
            var mobileDeviceSettingsServiceProvider = DataServiceContext.GetDataServiceProvider<mvMobileDeviceSettingsServiceProvider>();

            DateTime? syncDate = null;

            List<mvMobileDeviceSettings> serverDataList = null;

            var minSyncDate = (DateTimeOffset?)await mobileDeviceSettingsTableProvider.GetData().MinAsync(p => p.SyncDate);

            for (int i = 0; i < DbContext.SyncMaxAttempts; i++)
            {
                try
                {
                    if (!syncDate.HasValue) syncDate = await DataServiceContext.GetDatabaseServerUtcDateTimeAsync();

                    var query = mobileDeviceSettingsServiceProvider.GetData().AddQueryOption("$select", Selector).Where(await GetFilter());

                    if (minSyncDate.HasValue)
                        query = query.Where(p => (p.CreatedDate > minSyncDate.Value) || ((p.ModifiedDate != null) && (p.ModifiedDate.Value > minSyncDate.Value)));
                        
                    serverDataList = query.ToList();
                    break;
                }
                catch (Exception ex)
                {
                    if (i == (DbContext.SyncMaxAttempts - 1)) exception = ex;
                    else await Task.Delay(DbContext.SyncAttemptDelay);
                }
            }

            if (serverDataList != null)
            {
                int index = 0;
                foreach (var serverData in serverDataList)
                {
                    try
                    {
                        await ProcessLocalData(syncDate, 1, 1, serverData, null, mobileDeviceSettingsTableProvider, true);
                    }
                    catch (Exception ex)
                    {
                        if (!continueOnError)
                        {
                            exception = ex;
                            break;
                        }
                    }

                    index++;
                }
                
                await DbContext.UpdateAllSyncDateAsync<mvMobileDeviceSettings>(syncDate.Value);
            }

            return exception;
        }
        
        protected override async Task<Exception> OnUploadAllDataAsync(bool continueOnError)
        {
            Exception exception = null;

            var mobileDeviceSettingsTableProvider = DbContext.GetDataTableProvider<mvMobileDeviceSettingsTableProvider>();
            var mobileDeviceSettingsServiceProvider = DataServiceContext.GetDataServiceProvider<mvMobileDeviceSettingsServiceProvider>();

