        private async Task ProcessLocalData(DateTime? syncDate, int status, int attempts, mvMobileDeviceSettings serverData, mvMobileDeviceSettings obj,
            mvMobileDeviceSettingsTableProvider tableProvider, bool useTransaction)
        {
            var localDate = (syncDate.HasValue) ? syncDate.Value : await DbContext.GetDatabaseUtcDateTimeAsync();
            mvMobileDeviceSettings mobileDeviceSettings = null;

            if (serverData == null)
            {
                mobileDeviceSettings = new mvMobileDeviceSettings();
                mobileDeviceSettings.DeviceID = obj.DeviceID;

                await tableProvider.DeleteDataAsync(mobileDeviceSettings, useTransaction);
            }
            else
            {
                var isInsert = false;
                mobileDeviceSettings = await tableProvider.GetDataAsync(serverData.DeviceID);
                if (mobileDeviceSettings == null)
                {
                    isInsert = true;
                    mobileDeviceSettings = new mvMobileDeviceSettings();
                }                

                mobileDeviceSettings.CopyFrom(serverData);
                DbContext.SetSyncData(mobileDeviceSettings, syncDate, localDate, status, attempts);

                if (isInsert)
                    await tableProvider.InsertDataAsync(mobileDeviceSettings, useTransaction);
                else
                    await tableProvider.UpdateDataAsync(mobileDeviceSettings, useTransaction);

                if (obj != null)
                {
                    obj.CopyFrom(serverData);
                    DbContext.CopySyncData(obj, mobileDeviceSettings);
                }
            }
        }

