        private async Task ProcessLocalData(DateTime? syncDate, int status, int attempts, mvSalesPromotion serverData, mvSalesPromotion obj,
            mvSalesPromotionTableProvider tableProvider, bool useTransaction)
        {
            var localDate = (syncDate.HasValue) ? syncDate.Value : await DbContext.GetDatabaseUtcDateTimeAsync();
            mvSalesPromotion salesPromotion = null;

            if (serverData == null)
            {
                salesPromotion = new mvSalesPromotion();
                salesPromotion.ID = obj.ID;

                await tableProvider.DeleteDataAsync(salesPromotion, useTransaction);
            }
            else
            {
                var isInsert = false;
                var isDelete = false;
                salesPromotion = await tableProvider.GetDataAsync(serverData.ID);
                if (salesPromotion == null)
                {
                    isInsert = true;
                    salesPromotion = new mvSalesPromotion();
                }                
                else if (salesPromotion.IsDeleted)
                    isDelete = true;                    

                salesPromotion.CopyFrom(serverData);
                DbContext.SetSyncData(salesPromotion, syncDate, localDate, status, attempts);

                if (isInsert)
                    await tableProvider.InsertDataAsync(salesPromotion, useTransaction);
                else if (isDelete)
                    await tableProvider.DeleteDataAsync(salesPromotion, useTransaction);
                else
                    await tableProvider.UpdateDataAsync(salesPromotion, useTransaction);

                if (obj != null)
                {
                    obj.CopyFrom(serverData);
                    DbContext.CopySyncData(obj, salesPromotion);
                }
            }
        }

