using Microsoft.EntityFrameworkCore;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Linq.Expressions;
using System.Threading.Tasks;
using Wismapi.Data.Entity;

namespace AIO.IDOS3.Mobile.Data.Entity
{

    public partial class mvSalesmanSyncProvider : DataSyncProvider<mvSalesman>
    {

        #region Properties

        public static string Selector = "ID,Code,Name,Salesman,WarehouseID,WarehouseCode,WarehouseName,Warehouse,SiteID,GroupID,GroupName,CategoryID,CategoryName,Phone,StatusID,StatusName,CreatedDate,ModifiedDate,IsDeleted";

        #endregion

        #region Methods

        private async Task<Expression<Func<mvSalesman, bool>>> GetFilter()
        {
            var employeeID = await DbContext.GetDataTableProvider<mvEmployeeTableProvider>().GetData().Select(p => p.ID).SingleAsync();

            Expression filter = null;
            var type = typeof(mvSalesman);
            var param = Expression.Parameter(type, "param");

            var propEmployeeID = Expression.Property(param, "EmployeeID");
            filter = Expression.MakeBinary(ExpressionType.Equal, propEmployeeID, Expression.Constant(employeeID, type.GetProperty("EmployeeID").PropertyType));

            return Expression.Lambda<Func<mvSalesman, bool>>(filter, param);
        }



        protected override Exception OnDownloadAllData(bool continueOnError) { return OnDownloadAllDataAsync(continueOnError).Result; }
        protected override Exception OnUploadAllData(bool continueOnError) { return OnUploadAllDataAsync(continueOnError).Result; }
        protected override Exception OnDownloadData(mvSalesman obj, bool useTransaction) { return OnDownloadDataAsync(obj, useTransaction).Result; }
        protected override Exception OnUploadData(mvSalesman obj, bool useTransaction) { return OnUploadDataAsync(obj, useTransaction).Result; }


        protected override async Task<Exception> OnDownloadAllDataAsync(bool continueOnError)
        {
            Exception exception = null;

            var salesmanTableProvider = DbContext.GetDataTableProvider<mvSalesmanTableProvider>();
            var salesmanServiceProvider = DataServiceContext.GetDataServiceProvider<mvSalesmanServiceProvider>();

            DateTime? syncDate = null;

            List<mvSalesman> serverDataList = null;

            var minSyncDate = (DateTimeOffset?)await salesmanTableProvider.GetData().MinAsync(p => p.SyncDate);

            for (int i = 0; i < DbContext.SyncMaxAttempts; i++)
            {
                try
                {
                    if (!syncDate.HasValue) syncDate = await DataServiceContext.GetDatabaseServerUtcDateTimeAsync();

                    var query = salesmanServiceProvider.GetData().AddQueryOption("$select", Selector).Where(await GetFilter());

                    if (minSyncDate.HasValue)
                        query = query.Where(p => (p.CreatedDate > minSyncDate.Value) || ((p.ModifiedDate != null) && (p.ModifiedDate.Value > minSyncDate.Value)));

                    serverDataList = query.ToList();
                    break;
                }
                catch (Exception ex)
                {
                    if (i == (DbContext.SyncMaxAttempts - 1)) exception = ex;
                    else await Task.Delay(DbContext.SyncAttemptDelay);
                }
            }

            if (serverDataList != null)
            {
                if (serverDataList.Count > 0)
                {
                    var sites = await DbContext.GetDataTableProvider<mvSiteTableProvider>().GetData().ToListAsync();
                    var employee = await DbContext.GetDataTableProvider<mvEmployeeTableProvider>().GetData().FirstAsync();

                    int index = 0;
                    foreach (var serverData in serverDataList)
                    {
                        try
                        {
                            var site = sites.Where(p => p.ID.Equals(serverData.SiteID)).SingleOrDefault();

                            AssignFromLocalData(serverData, site, employee);

                            await ProcessLocalData(syncDate, 1, 1, serverData, null, salesmanTableProvider, true);
                        }
                        catch (Exception ex)
                        {
                            if (!continueOnError)
                            {
                                exception = ex;
                                break;
                            }
                        }

                        index++;
                    }
                }

                await DbContext.UpdateAllSyncDateAsync<mvSalesman>(syncDate.Value);
            }

            return exception;
        }

        protected override async Task<Exception> OnUploadAllDataAsync(bool continueOnError)
        {
            Exception exception = new NotImplementedException();

            return await Task.FromResult(exception);
        }


        protected override async Task<Exception> OnDownloadDataAsync(mvSalesman obj, bool useTransaction)
        {
            Exception exception = null;

            var salesmanTableProvider = DbContext.GetDataTableProvider<mvSalesmanTableProvider>();
            var salesmanServiceProvider = DataServiceContext.GetDataServiceProvider<mvSalesmanServiceProvider>();

            DateTime? syncDate = null;
            int status = 1;
            int attempts = 0;

            mvSalesman serverData = null;

            for (int i = 0; i < DbContext.SyncMaxAttempts; i++)
            {
                try
                {
                    if (!syncDate.HasValue) syncDate = await DataServiceContext.GetDatabaseServerUtcDateTimeAsync();

                    serverData = salesmanServiceProvider.GetData().AddQueryOption("$select", Selector)
                        .Where(p => p.ID.Equals(obj.ID)).SingleOrDefault();
                    break;
                }
                catch (Exception ex)
                {
                    if (i == (DbContext.SyncMaxAttempts - 1)) { exception = ex; status = 0; }
                    else await Task.Delay(DbContext.SyncAttemptDelay);
                }
            }

            if (serverData != null)
            {
                var site = await DbContext.GetDataTableProvider<mvSiteTableProvider>().GetDataAsync(serverData.WarehouseID);
                var employee = await DbContext.GetDataTableProvider<mvEmployeeTableProvider>().GetData().FirstAsync();

                AssignFromLocalData(serverData, site, employee);
            }

            await ProcessLocalData(syncDate, status, attempts, serverData, obj, salesmanTableProvider, useTransaction);

            return exception;
        }

        protected override async Task<Exception> OnUploadDataAsync(mvSalesman obj, bool useTransaction)
        {
            Exception exception = new NotImplementedException();

            return await Task.FromResult(exception);
        }



        private async Task ProcessLocalData(DateTime? syncDate, int status, int attempts, mvSalesman serverData, mvSalesman obj,
            mvSalesmanTableProvider tableProvider, bool useTransaction)
        {
            var localDate = (syncDate.HasValue) ? syncDate.Value : await DbContext.GetDatabaseUtcDateTimeAsync();
            mvSalesman salesman = null;

            if (serverData == null)
            {
                salesman = new mvSalesman();
                salesman.ID = obj.ID;

                await tableProvider.DeleteDataAsync(salesman, useTransaction);
            }
            else
            {
                var isInsert = false;
                var isDelete = false;
                salesman = await tableProvider.GetDataAsync(serverData.ID);
                if (salesman == null)
                {
                    isInsert = true;
                    salesman = new mvSalesman();
                }
                else if (salesman.IsDeleted)
                    isDelete = true;

                salesman.CopyFrom(serverData);
                DbContext.SetSyncData(salesman, syncDate, localDate, status, attempts);

                if (isInsert)
                    await tableProvider.InsertDataAsync(salesman, useTransaction);
                else if (isDelete)
                    await tableProvider.DeleteDataAsync(salesman, useTransaction);
                else
                    await tableProvider.UpdateDataAsync(salesman, useTransaction);

                if (obj != null)
                {
                    obj.CopyFrom(serverData);
                    DbContext.CopySyncData(obj, salesman);
                }
            }
        }

        private void AssignFromLocalData(mvSalesman serverData, mvSite site, mvEmployee employee)
        {
            if (site != null)
            {
                serverData.SiteCode = site.Code;
                serverData.SiteName = site.Name;
                serverData.Site = site.Site;
                serverData.AreaID = site.AreaID;
                serverData.AreaCode = site.AreaCode;
                serverData.AreaName = site.AreaName;
                serverData.Area = site.Area;
                serverData.RegionID = site.RegionID;
                serverData.RegionCode = site.RegionCode;
                serverData.RegionName = site.RegionName;
                serverData.Region = site.Region;
                serverData.TerritoryID = site.TerritoryID;
                serverData.TerritoryCode = site.TerritoryCode;
                serverData.TerritoryName = site.TerritoryName;
                serverData.Territory = site.Territory;
                serverData.CompanyID = site.CompanyID;
                serverData.CompanyCode = site.CompanyCode;
                serverData.CompanyName = site.CompanyName;
                serverData.Company = site.Company;
                serverData.SiteDistributionTypeID = site.DistributionTypeID;
                serverData.SiteDistributionTypeName = site.DistributionTypeName;
                serverData.IsSiteProductLotCodeEntryRequired = site.IsProductLotCodeEntryRequired;
            }

            if (employee != null)
            {
                serverData.EmployeeCode = employee.Code;
            }
        }

        #endregion

    }

