        protected override async Task<Exception> OnDownloadAllDataAsync(bool continueOnError)
        {
            Exception exception = null;

            var userSiteTableProvider = DbContext.GetDataTableProvider<mvUserSiteTableProvider>();
            var userSiteServiceProvider = DataServiceContext.GetDataServiceProvider<mvUserSiteServiceProvider>();

            DateTime? syncDate = null;

            List<mvUserSite> serverDataList = null;

            var minSyncDate = (DateTimeOffset?)await userSiteTableProvider.GetData().MinAsync(p => p.SyncDate);

            for (int i = 0; i < DbContext.SyncMaxAttempts; i++)
            {
                try
                {
                    if (!syncDate.HasValue) syncDate = await DataServiceContext.GetDatabaseServerUtcDateTimeAsync();

                    var query = userSiteServiceProvider.GetData().AddQueryOption("$select", Selector).Where(await GetFilter());

                    if (minSyncDate.HasValue)
                        query = query.Where(p => (p.CreatedDate > minSyncDate.Value));
                        
                    serverDataList = query.ToList();
                    break;
                }
                catch (Exception ex)
                {
                    if (i == (DbContext.SyncMaxAttempts - 1)) exception = ex;
                    else await Task.Delay(DbContext.SyncAttemptDelay);
                }
            }

            if (serverDataList != null)
            {
                int index = 0;
                foreach (var serverData in serverDataList)
                {
                    try
                    {
                        await ProcessLocalData(syncDate, 1, 1, serverData, null, userSiteTableProvider, true);
                    }
                    catch (Exception ex)
                    {
                        if (!continueOnError)
