        private async Task ProcessLocalData(DateTime? syncDate, int status, int attempts, mvUserSite serverData, mvUserSite obj,
            mvUserSiteTableProvider tableProvider, bool useTransaction)
        {
            var localDate = (syncDate.HasValue) ? syncDate.Value : await DbContext.GetDatabaseUtcDateTimeAsync();
            mvUserSite userSite = null;

            if (serverData == null)
            {
                userSite = new mvUserSite();
                userSite.SiteID = obj.SiteID;
                userSite.UserID = obj.UserID;

                await tableProvider.DeleteDataAsync(userSite, useTransaction);
            }
            else
            {
                var isInsert = false;
                userSite = await tableProvider.GetDataAsync(serverData.SiteID, serverData.UserID);
                if (userSite == null)
                {
                    isInsert = true;
                    userSite = new mvUserSite();
                }                

                userSite.CopyFrom(serverData);
                DbContext.SetSyncData(userSite, syncDate, localDate, status, attempts);

                if (isInsert)
                    await tableProvider.InsertDataAsync(userSite, useTransaction);
                else
                    await tableProvider.UpdateDataAsync(userSite, useTransaction);

                if (obj != null)
                {
                    obj.CopyFrom(serverData);
                    DbContext.CopySyncData(obj, userSite);
                }
            }
        }

