        private async Task ProcessLocalData(DateTime? syncDate, int status, int attempts, mvUserDeviceLocation serverData, mvUserDeviceLocation obj,
            mvUserDeviceLocationTableProvider tableProvider, bool useTransaction)
        {
            var localDate = (syncDate.HasValue) ? syncDate.Value : await DbContext.GetDatabaseUtcDateTimeAsync();
            mvUserDeviceLocation userDeviceLocation = null;

            if (serverData == null)
