                userDeviceLocation.CopyFrom(serverData);
                DbContext.SetSyncData(userDeviceLocation, syncDate, localDate, status, attempts);

                if (isInsert)
                    await tableProvider.InsertDataAsync(userDeviceLocation, useTransaction);
                else if (isDelete)
                    await tableProvider.DeleteDataAsync(userDeviceLocation, useTransaction);
                else
                    await tableProvider.UpdateDataAsync(userDeviceLocation, useTransaction);

