using AIO.IDOS3.Data.Entity;
using Microsoft.AspNet.OData;
using Microsoft.AspNet.OData.Routing;
using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Logging;

namespace AIO.IDOS3.Web.Services.Controllers.DataServices.Common
{

    public class CommonsController : ODataController
    {

        #region Fields

        protected readonly ILogger<CommonsController> logger;
        protected readonly MainDbContext context;

        #endregion

        #region Constructors

        public CommonsController(ILogger<CommonsController> logger, MainDbContext context)
        {
            this.logger = logger;
            this.context = context;
        }

        #endregion

        #region Methods
        
        [HttpGet]
        [ODataRoute("GetDatabaseServerDateTime()")]
        public IActionResult GetDatabaseServerDateTime()
        {                        
            return Ok(context.GetDatabaseDateTime());
        }

        [HttpGet]
        [ODataRoute("GetDatabaseServerUtcDateTime()")]
        public IActionResult GetDatabaseServerUtcDateTime()
        {
            return Ok(context.GetDatabaseUtcDateTime());
        }

        #endregion

    }

